import { useForm } from "react-hook-form";

export default function ChoiseButton({decrement,increment,value,title,register,test}){
    return(
        <div class="custom-number-input h-10 w-full p-5 justify-center flex">
            <label for="custom-input-number" class="w-full text-gray-700 text-sm font-semibold ml-2 mt-3">
            {title}
            </label>
        <div class="flex flex-row h-10 w-full rounded-lg relative bg-transparent mt-1">
            <button onClick={decrement} class=" bg-gray-300 text-gray-600 hover:text-gray-700 hover:bg-gray-400 h-full w-20 rounded-l cursor-pointer outline-none">
                <span class="m-auto text-2xl font-thin">âˆ’</span>
            </button>
        <input {...register(test)} type="number" class="focus:outline-none text-center w-full bg-gray-300 font-semibold text-md hover:text-black focus:text-black  md:text-basecursor-default flex items-center text-gray-700  outline-none" name="custom-input-number" value={value}></input>
            <button onClick={increment} class="bg-gray-300 text-gray-600 hover:text-gray-700 hover:bg-gray-400 h-full w-20 rounded-r cursor-pointer">
                <span class="m-auto text-2xl font-thin">+</span>
            </button>
        </div>
    </div>
    )
}
