import React, { useState } from "react";
import { useForm } from "react-hook-form";

import ChoiseButton from "../composants/ChoiseButton";

function TimerSelector(){

    const { register, handleSubmit } = useForm();
    const onSubmit = (data, e) => console.log(data);
    const onError = (errors, e) => console.log(errors, e);

    const [roundNumber,setRoundNumber]= useState(0)

    const addRound= () =>{
        setRoundNumber(roundNumber+1)
    }
    const deleteRound= () =>{
        if(roundNumber==0){
            return
        } else{
            setRoundNumber(roundNumber-1)
        }

    }


    return (
        <form onSubmit={handleSubmit(onSubmit, onError)} className="mx-auto mt-3 flex flex-col">
            <ChoiseButton
                decrement = {deleteRound}
                increment ={addRound}
                value = {roundNumber}
                title = 'Nombres de Tours'
                register = {register}
                test = "rounds"
            />
            <button className="mt-10 justify-end bg-blue-600 mx-4 text-white rounded-3xl" type="submit">Valider</button>
        </form>
    )
}



function Tabata(){
    return(
        <TimerSelector />
    )
}
export default Tabata
