import React, { useEffect, useState } from "react";
import ReactAudioPlayer from "react-audio-player";

import { useContext } from "react";
import { Context } from "../store/Store";
import * as ACTIONS from "../store/actions/actions";

import Layout from "../layout/Layout";

function Workout(){

    const { app, dispatch } = React.useContext(Context);
    const [workout,setWorkout] = useState({
        rounds : null,
        worktime : null,
        resttime : null,
        work : true,
        finish : false
    })

    useEffect(()=>{
        setWorkout(app.workout)
    })

    return(
        <Layout
            color="bg-green-100"
        >
            <svg id="animated" viewbox="0 0 100 100">
  <circle cx="50" cy="50" r="45" fill="#FDB900"/>
  <path fill="none" stroke-linecap="round" stroke-width="5" stroke="#fff"
        stroke-dasharray="251.2,0"
        d="M50 10
           a 40 40 0 0 1 0 80
           a 40 40 0 0 1 0 -80">
    <animate attributeName="stroke-dasharray" from="0,251.2" to="251.2,0" dur="5s"/>           
  </path>
  <text id="count" x="50" y="50" text-anchor="middle" dy="7" font-size="20">100%</text>
</svg>
        </Layout>
    )
}

export default Workout
